targets.expr:
max(
  node_memory_MemTotal_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
  - node_memory_MemFree_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
  - node_memory_Buffers_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
  - node_memory_Cached_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
)

max(
  (
    (
      node_memory_MemTotal_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
    - node_memory_MemFree_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
    - node_memory_Buffers_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
    - node_memory_Cached_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
    )
    / node_memory_MemTotal_bytes{cluster="$cluster", job="node-exporter", instance="$instance"}
  ) * 100)

max(
  node_filesystem_files{cluster="$cluster", job="node-exporter", instance="$instance"}
  - node_filesystem_files_free{cluster="$cluster", job="node-exporter", instance="$instance"}
)

max(
  (
    (
      node_filesystem_files{cluster="$cluster", job="node-exporter", instance="$instance"}
    - node_filesystem_files_free{cluster="$cluster", job="node-exporter", instance="$instance"}
    )
    / node_filesystem_files{cluster="$cluster", job="node-exporter", instance="$instance"}
  ) * 100)
